import React from 'react'

class Form extends React.Component {
    render() {
        return (
            <form onSubmit={this.handleSubmit}>
            <label>
              Nome:
              <input type="text" value={this.state.value} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Enviar" />
          </form>
        )
    }
}

export default Form;
