import React from 'react'

class Form extends React.Component {
  constructor() {
    super()
    this.state = {
      name: '',
      email: '',
    }
  }
  render() {
    const {name, email} = this.state
    return (
      <form>
        <label>
          Nome:
          <input type="text" value={name}></input>
        </label>
        <label>
          Email:
          <input type="text" value={email}></input>
        </label>
      </form>
    )
  }
}

export default Form;
