import React from "react";

class Usuario extends React.Component {
  constructor() {
    super();
    this.state = {
      api: [],
      name: '',
      cell: '',
      age: '',
      gender: '',
      picture: '',

    }
  }

  fetchApi = async () => {
    const url = 'https://api.randomuser.me/'
    const response = await fetch(url);
    const dataJson = await response.json();
    const data = await dataJson.results[0]
    const { name, cell, dob: { age }, picture } = data;
    this.setState({
      api: data,
      name,
      cell,
      age,
      picture: picture.medium
      // gender: data,
      // picture: data,
    });
  }

  async componentDidMount() {
    this.fetchApi();
  }

  render() {
    const { api, name: {first, last, title}, cell, age, picture } = this.state
    console.log(api)
    // console.log(Object.values(api)[0].email)
    return (
      <div>
        <p>{ title } {first} {last} {age}</p>
        <p>{ cell } </p>
        <img src={ picture } alt="nada"/>
        <p>BATATA</p>
      </div>
    )
  }
}

export default Usuario;
